<script>
export default {
  name: "FooterGuide",
  methods: {
    data() {
      return {};
    },
    goto(path) {
      // this.$route.path 取当前组件的路由地址，$route是当前组件的路由
      if (this.$route.path !== path) {
        // 这是编程式路由，$router是main.js中注册的路由器
        this.$router.replace(path);
      }
    }
  }
};
</script>
